function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),c=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),c.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked"))}))}function fixedHeader(){const e=document.querySelector("header");window.scrollY>=100?e.classList.add("fixed__header"):e.classList.remove("fixed__header")}burgerMenu(),window.addEventListener("scroll",fixedHeader);